Rule = Struct.new('Rule', :element, :compound)
rules = <<END.scan(/^(.+) => (.+)$/).map {|element, compound| Rule.new(element, compound)}
Al => ThF
Al => ThRnFAr
B => BCa
B => TiB
B => TiRnFAr
Ca => CaCa
Ca => PB
Ca => PRnFAr
Ca => SiRnFYFAr
Ca => SiRnMgAr
Ca => SiTh
F => CaF
F => PMg
F => SiAl
H => CRnAlAr
H => CRnFYFYFAr
H => CRnFYMgAr
H => CRnMgYFAr
H => HCa
H => NRnFYFAr
H => NRnMgAr
H => NTh
H => OB
H => ORnFAr
Mg => BF
Mg => TiMg
N => CRnFAr
N => HSi
O => CRnFYFAr
O => CRnMgAr
O => HP
O => NRnFAr
O => OTi
P => CaP
P => PTi
P => SiRnFAr
Si => CaSi
Th => ThCa
Ti => BP
Ti => TiTi
e => HF
e => NAl
e => OMg
END

#taken from reddit, solution by /u/hagabaka
product = DATA.read.strip

steps = 0
loop do
  rules.shuffle!
  exhausted = rules.none? do |rule|
    if product.include?(rule.compound)
      product.sub!(rule.compound, rule.element)
      steps += 1
      printf "%4i %s \n", steps, product.inspect
      true
    end
  end
  if product == 'e'
    puts "Answer <= #{steps}"
    exit 0
  elsif exhausted
    exit 1
  end
end
__END__

